        "publisher":{
            "@type": "Organization",
            "@id": "#organization"
        },
        "articleBody":"España, asegura Sánchez, está preparada para &quot;empezar a producir bienes cada vez más complejos&quot; y además destaca la &quot;mejora del capital humano&quot;.",
        "image": [{
